#! /usr/local/bin/node

import { Configuration, Linter } from "tslint";
import * as FS from "fs";
import * as lib from '../index'

const fileName = "../lintAndCommit.ts";

const configurationFilename = "tslint.json";
const options = {
  fix: false,
  formatter: "json",
  rulesDirectory: "customRules/",
  formattersDirectory: "customFormatters/"
};

const fileContents = <string>FS.readFileSync(fileName, {encoding:"utf-8"});

const linter = new Linter(options);
const configuration = Configuration.findConfiguration(configurationFilename, fileName).results;
linter.lint(fileName, fileContents, configuration);
const result = linter.getResult();

//FS.writeFileSync('log.txt',JSON.stringify(tsResultsPrint(result)),{encoding:"utf-8"});
tsResultsPrint(result);

function logKeys (objToLog) {
  let stringVal = '';
  Object.keys(objToLog).forEach((value)=>{
    console.log('Value = ' + value);
    let valToLog: object = objToLog[value];
    stringVal += value + ' : ' + valToLog + ',\n';
  });
  return stringVal;
}

function tsResultsPrint (results) {
  let exceptions = [];

  if (results.failures.length > 0) {
    for (let i = 0; i < 1; i++) {
      let message = results.failures[i];
      let exception: lib.lintException = {
        line: message.rowLines,
        column: message.startPosition,
        rule: message.ruleName,
        excerpt: null
      };
      exceptions.push(exception);
    }
  }
  console.log('exceptions = ' + logKeys(exceptions[0]));
  console.log('errors = ' + results.errorCount);
  console.log('warnings = ' + results.warningCount);
  return {
    errors: results.errorCount,
    warnings: results.warningCount,
    exceptions: exceptions
  }
}

